package workspace;

public class test01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 제어문 : 조건문 if 
		int score = 80;
		
		// 점수가 60점 이상이면 합격
		if(score >= 60) {
			System.out.println("합격 !");
		}else {
			// 결과가 거짓일때 
			System.out.println("불합격");
		}
		
	}

}
